import { withAuthenticator } from "@aws-amplify/ui-react";
import "@aws-amplify/ui-react/styles.css";
import { useState } from "react";
import { Auth, API, Amplify } from "aws-amplify";
import config from "./../aws-exports.js";
Auth.configure(config);

async function download_file(filename) {
  const user = await Auth.currentAuthenticatedUser();
  const token = user.signInUserSession.idToken.jwtToken;
}

async function callApiDynamo(filename, date) {
  console.log("PRINTING EVENT");
  const user = await Auth.currentAuthenticatedUser();
  const token = user.signInUserSession.idToken.jwtToken;

  const requestData = {
    headers: {
      Authorization: "Bearer " + token,
    },

    queryStringParameters: {
      requested_filename: filename,
      upload_date: date,
    },
  };
  console.log(requestData);
  try {
    const data = await API.get("phibyeapi", "/pulldata", requestData);
    return data;
  } catch (error) {
    console.log(error);
  }
}

function MyForm() {
  const [filename, setfileName] = useState("");
  const [date, setDate] = useState("");
  const [filetable, setFileTable] = useState([]);
  const handleSubmit = (event) => {
    event.preventDefault();
    if (filename.length === 0 && date.length === 0) {
      alert("Fields file name & date cannot be empty.");
      return;
    }
    alert(`Name inputted: ${filename}\nDate inputted: ${date}`);
    callApiDynamo(filename, date).then((response) => {
      /* ACCESS DYNAMODB ITEMS HERE */
      console.log(response);
      setFileTable(response);
    });
  };
  return (
    <div>
      <h1>File Lookup Page</h1>
      <form onSubmit={handleSubmit}>
        <div class="input-group">
          <label class="dialog-box">
            Enter Filename:
            <input
              class="input"
              type="text"
              value={filename}
              onChange={(e) => setfileName(e.target.value)}
            />
          </label>
          <br></br>
          <label class="dialog-box">
            Enter Date:
            <input
              class="input"
              type="Date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
          </label>
        </div>
        <br></br>
        <input className="submit-btn" type="submit" />
      </form>
      <br></br>
      <div className="app-container">
        <table>
          <thead>
            <tr>
              <th>File Name</th>
              <th>File Type</th>
              <th>Redacted</th>
              <th>Upload Date</th>
            </tr>
          </thead>
          <tbody>
            {filetable.map((fileinfo) => (
              <tr>
                <td>{fileinfo.original_filename}</td>
                <td>{fileinfo.filetype}</td>
                <td>{String(fileinfo.redacted)}</td>
                <td>{fileinfo.upload_date}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

